INSERT INTO `news` (`id`, `title`, `description`, `created_at`, `updated_at`) VALUES
(618, 'UPDATE 1-China\'s BYD H1 profit triples as deliveries break record', 'China\'s BYD Co Ltd on Monday said first-half profit jumped 204.7% as the new energy vehicle maker broke its delivery record and retained its crown as China\'s biggest-selling auto brand.  Net earnings for the six-months ending June 30 reached 10.95 billion yuan ($1.50 billion), up 204.7% from 3.6 billion yuan a year earlier, on a 72.7% rise in revenue to 260.12 billion yuan, BYD said in a stock market filing.  The company posted a 6.82 billion yuan net profit for the April-June quarter, up 144.7%, a Reuters calculation showed.', '2023-08-29 06:05:17', '2023-08-29 06:05:17'),
(619, 'Dow Jones Futures Rise As Rally Attempt Faces Big Questions; Tesla Cybertruck Buzz Builds', 'Dow Jones futures: Despite Thursday\'s ugly reversal, the market rally attempt is still fighting. Nvidia, Tesla lead stocks to watch.', '2023-08-29 06:05:17', '2023-08-29 06:05:17'),
